import React from 'react';

/**
 * Array de productos con textos en español e inglés.
 * Nota: "idInterno" debe coincidir con las claves que usas en "getProductByName"
 */
const productList = [
  {
    idInterno: "Shampoo Signature", // Mismo string que usas al hacer push en la lógica
    name: {
      es: "Shampoo Signature",
      en: "Signature Shampoo",
    },
    description: {
      es: "Un limpiador diario rico, con ingredientes exclusivos para suavizar y proteger.",
      en: "A rich daily cleanser with exclusive ingredients to soften and protect your hair.",
    },
    mainBenefit: {
      es: "Ligero",
      en: "Lightweight",
    },
    secondaryBenefits: {
      es: "Limpiador rico y diario, repara y protege el cabello.",
      en: "Rich daily wash that repairs and protects hair.",
    },
  },
  {
    idInterno: "Shampoo Equilibrium",
    name: {
      es: "Shampoo Equilibrium",
      en: "Equilibrium Shampoo",
    },
    description: {
      es: "Extiende el tiempo entre lavados, reduce el exceso de grasa y equilibra el cuero cabelludo.",
      en: "Extends time between washes, reduces excess oil, and balances the scalp.",
    },
    mainBenefit: {
      es: "Regulación del exceso de grasa",
      en: "Regulates excess oil",
    },
    secondaryBenefits: {
      es: "Controla sebo, prolonga estilos y equilibra microbioma.",
      en: "Controls sebum, prolongs styles, and balances microbiome.",
    },
  },
  {
    idInterno: "Acondicionador Signature",
    name: {
      es: "Acondicionador Signature",
      en: "Signature Conditioner",
    },
    description: {
      es: "Hidratante restaurador diario. Facilita desenredar y prolonga la suavidad del cabello.",
      en: "Daily restorative moisturizer. Easily detangles and keeps hair soft throughout the day.",
    },
    mainBenefit: {
      es: "Repara y protege",
      en: "Repairs and protects",
    },
    secondaryBenefits: {
      es: "Intenso pero ligero, revive el brillo.",
      en: "Rich yet lightweight, revives shine.",
    },
  },
  {
    idInterno: "Mascarilla Hidratante",
    name: {
      es: "Mascarilla Hidratante",
      en: "Hydrating Mask",
    },
    description: {
      es: "Ultra hidratante con aceites de coco y almendra para cabello seco o rizado.",
      en: "Ultra-hydrating mask with coconut and almond oils for dry or curly hair.",
    },
    mainBenefit: {
      es: "Previene la rotura",
      en: "Prevents breakage",
    },
    secondaryBenefits: {
      es: "Sella cutícula, repara daños, restaura elasticidad y combate frizz.",
      en: "Seals cuticle, repairs damage, restores elasticity, and fights frizz.",
    },
  },
  {
    idInterno: "Mascarilla Reconstrucción",
    name: {
      es: "Mascarilla Reconstrucción",
      en: "Reconstruction Mask",
    },
    description: {
      es: "Reconstruye el cabello dañado con té blanco, baobab, jazmín y complejo bio-restaurador.",
      en: "Rebuilds damaged hair with white tea, baobab, jasmine, and a bio-restorative complex.",
    },
    mainBenefit: {
      es: "Nutrición intensa y acondicionamiento",
      en: "Intense nutrition and conditioning",
    },
    secondaryBenefits: {
      es: "Fortalece cutícula, mejora elasticidad, deja el cabello sedoso.",
      en: "Strengthens cuticle, improves elasticity, leaves hair silky.",
    },
  },
  {
    idInterno: "Leave-in Signature",
    name: {
      es: "Leave-in Signature",
      en: "Signature Leave-In",
    },
    description: {
      es: "Bálsamo ligero para definir estilos sin calor y proteger contra daños.",
      en: "Lightweight balm to define heat-free styles and protect against damage.",
    },
    mainBenefit: {
      es: "Define estilos sin calor",
      en: "Defines styles without heat",
    },
    secondaryBenefits: {
      es: "Suaviza, pule, controla frizz y fortalece cabello frágil.",
      en: "Softens, polishes, tames frizz, and strengthens fragile hair.",
    },
  },
  {
    idInterno: "Aceite Amplificador",
    name: {
      es: "Aceite Amplificador",
      en: "Amplifying Oil",
    },
    description: {
      es: "Aceite ligero de absorción rápida, protege contra daños y aporta brillo.",
      en: "Lightweight fast-absorbing oil that shields from damage and adds shine.",
    },
    mainBenefit: {
      es: "Aceite ligero y lujoso",
      en: "Luxurious lightweight oil",
    },
    secondaryBenefits: {
      es: "Penetra fibra capilar, reduce tiempo de secado y evita apelmazar.",
      en: "Penetrates hair shaft, reduces drying time, and avoids weigh-down.",
    },
  },
  {
    idInterno: "Bálsamo para Rizos",
    name: {
      es: "Bálsamo para Rizos",
      en: "Curl Balm",
    },
    description: {
      es: "Gel ligero para definir y dar brillo a rizos, combatiendo humedad y frizz.",
      en: "Light gel that defines and adds shine to curls, fighting humidity and frizz.",
    },
    mainBenefit: {
      es: "Fórmula ligera con fijación natural",
      en: "Light formula with natural hold",
    },
    secondaryBenefits: {
      es: "Hidrata, acondiciona y mantiene la memoria del rizo todo el día.",
      en: "Hydrates, conditions, and preserves curl memory all day.",
    },
  },
];

/**
 * Busca producto por su "idInterno" (la misma cadena usada en la lógica).
 */
function getProductByInternalName(internalName) {
  return productList.find((p) => p.idInterno === internalName);
}

function FinalScreen({ formData, language }) {
  /**
   * Lógica de recomendación.
   * Nota: En tu Questionnaire.jsx, haces push con un string tipo:
   *   recs.push(getProductByInternalName('Mascarilla Hidratante'));
   * Ajusta los nombres internos para que coincidan con "idInterno" arriba.
   */
  const getRecommendations = (data) => {
    let recs = [];

    // Ejemplo (ajusta según tu lógica real):
    if (data.condicionGeneral === 'seco-quebradizo') {
      recs.push(getProductByInternalName('Mascarilla Hidratante'));
      recs.push(getProductByInternalName('Aceite Amplificador'));
    }

    if (data.condicionGeneral === 'graso') {
      recs.push(getProductByInternalName('Shampoo Equilibrium'));
    }

    if ((data.preocupacionesCapilares || []).includes('falta-volumen')) {
      recs.push(getProductByInternalName('Leave-in Signature'));
    }

    if ((data.preocupacionesCapilares || []).includes('frizz')) {
      recs.push(getProductByInternalName('Bálsamo para Rizos'));
      recs.push(getProductByInternalName('Aceite Amplificador'));
    }

    if (
      data.tratamientosQuimicos === 'tinte-decoloracion' ||
      data.tratamientosQuimicos === 'alisado-permanente'
    ) {
      recs.push(getProductByInternalName('Mascarilla Reconstrucción'));
      recs.push(getProductByInternalName('Acondicionador Signature'));
    }

    if (data.frecuenciaLavado === 'diario' || data.frecuenciaLavado === 'cada-dos-dias') {
      recs.push(getProductByInternalName('Shampoo Signature'));
      recs.push(getProductByInternalName('Shampoo Equilibrium'));
    }

    if (data.usoCalor === 'ocasional' || data.usoCalor === 'frecuente') {
      recs.push(getProductByInternalName('Aceite Amplificador'));
      recs.push(getProductByInternalName('Leave-in Signature'));
    }

    // Filtrar duplicados
    const uniqueRecs = recs.filter(Boolean);
    return [...new Set(uniqueRecs)];
  };

  const recommendations = getRecommendations(formData);

  return (
    <div className="final-screen">
      <h1>
        {language === 'es'
          ? `¡Gracias, ${formData.nombre || 'Usuario'}!`
          : `Thank you, ${formData.nombre || 'User'}!`}
      </h1>

      <p>
        {language === 'es'
          ? 'Basado en tus respuestas, te recomendamos:'
          : 'Based on your answers, we recommend:'}
      </p>

      {/* Lista de productos en formato bilingüe */}
      {recommendations.map((product) => {
        // Seleccionamos los textos correctos según language
        const productName =
          language === 'es' ? product.name.es : product.name.en;
        const productDescription =
          language === 'es' ? product.description.es : product.description.en;
        const productMainBenefit =
          language === 'es' ? product.mainBenefit.es : product.mainBenefit.en;
        const productSecondaryBenefits =
          language === 'es'
            ? product.secondaryBenefits.es
            : product.secondaryBenefits.en;

        return (
          <div key={product.idInterno} style={{ marginBottom: '1rem' }}>
            <strong>{productName}</strong>
            <br />
            <em>{productDescription}</em>
            <br />
            <strong>
              {language === 'es' ? 'Beneficio Principal:' : 'Main Benefit:'}
            </strong>{' '}
            {productMainBenefit}
            <br />
            <strong>
              {language === 'es'
                ? 'Beneficios Secundarios:'
                : 'Secondary Benefits:'}
            </strong>{' '}
            {productSecondaryBenefits}
          </div>
        );
      })}
    </div>
  );
}

export default FinalScreen;
