import React, { useState } from 'react';
import './App.css';

// IMPORTAMOS la textura de cuero gris oscuro
import darkLeather from './assets/blueLeather.jpg';

import Questionnaire from './components/Questionnaire';
import SplashScreen from './components/SplashScreen'; // Extraemos SplashScreen a un componente aparte (opcional)

function App() {
  const [showQuestionnaire, setShowQuestionnaire] = useState(false);

  const handleStart = () => {
    setShowQuestionnaire(true);
  };

  // Estilos de fondo con la textura gris oscuro
  const backgroundStyle = {
    backgroundImage: `url(${darkLeather})`,
    backgroundRepeat: 'no-repeat',
    backgroundSize: 'cover',
    backgroundPosition: 'center center',
    backgroundAttachment: 'fixed',
    minHeight: '100vh',
  };

  return (
    <div style={backgroundStyle}>
      <div className="app-container">
        {showQuestionnaire ? (
          <Questionnaire />
        ) : (
          <SplashScreen onStart={handleStart} />
        )}
      </div>
    </div>
  );
}

export default App;
