import React, { useState } from 'react';
import './App.css';

function App() {
  // Controlamos la pantalla de presentación vs. cuestionario
  const [showQuestionnaire, setShowQuestionnaire] = useState(false);

  // Manejador del botón "Empezar Cuestionario"
  const handleStart = () => {
    setShowQuestionnaire(true);
  };

  return (
    <div className="app-container">
      {showQuestionnaire ? (
        /* Si showQuestionnaire es true, mostramos el cuestionario */
        <Questionnaire />
      ) : (
        /* Si no, mostramos la pantalla de presentación con el logo */
        <SplashScreen onStart={handleStart} />
      )}
    </div>
  );
}

/** 
 * Componente SplashScreen 
 * Muestra el logo, un título y el botón "Empezar Cuestionario"
 */
function SplashScreen({ onStart }) {
  return (
    <div className="splash-screen">
      <img src="/fi-logo.png" alt="Logo Francisco Iglesias" className="logo-animado" />
      <h1 className="titulo-splash">franciscoiglesias.cuestionario</h1>
      <button className="btn-empezar" onClick={onStart}>Empezar Cuestionario</button>
    </div>
  );
}

/**
 * Componente Questionnaire
 * Representa tu cuestionario (el que hiciste antes).
 * Aquí puedes colocar tus campos, preguntas, etc.
 */
function Questionnaire() {
  const [formData, setFormData] = useState({
    nombre: '',
    email: '',
    tipoCabello: '',
    frecuenciaLavado: '',
    problemasCabello: '',
    productosUsados: '',
    coloracion: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    alert('¡Gracias por completar el cuestionario! Pronto recibirás un diagnóstico por correo.');
    // Lógica para enviar datos a backend o procesar
    setFormData({
      nombre: '',
      email: '',
      tipoCabello: '',
      frecuenciaLavado: '',
      problemasCabello: '',
      productosUsados: '',
      coloracion: ''
    });
  };

  return (
    <div className="container">
      <h1>Cuestionario de Diagnóstico Capilar</h1>
      <h2>Francisco Iglesias</h2>

      <p>
        Por favor, completa los siguientes datos y responde a las preguntas 
        para obtener un diagnóstico de tu cabello.
      </p>

      <form onSubmit={handleSubmit}>
        <label htmlFor="nombre">Nombre completo:</label>
        <input
          id="nombre"
          name="nombre"
          type="text"
          placeholder="Ingresa tu nombre"
          value={formData.nombre}
          onChange={handleChange}
          required
        />

        <label htmlFor="email">Correo electrónico:</label>
        <input
          id="email"
          name="email"
          type="email"
          placeholder="tucorreo@ejemplo.com"
          value={formData.email}
          onChange={handleChange}
          required
        />

        <label htmlFor="tipoCabello">1. ¿Cómo describirías tu tipo de cabello?</label>
        <select
          id="tipoCabello"
          name="tipoCabello"
          value={formData.tipoCabello}
          onChange={handleChange}
          required
        >
          <option value="" disabled>Selecciona una opción</option>
          <option value="seco">Seco</option>
          <option value="graso">Graso</option>
          <option value="normal">Normal</option>
          <option value="mixto">Mixto</option>
        </select>

        <label htmlFor="frecuenciaLavado">2. ¿Con qué frecuencia lavas tu cabello?</label>
        <select
          id="frecuenciaLavado"
          name="frecuenciaLavado"
          value={formData.frecuenciaLavado}
          onChange={handleChange}
          required
        >
          <option value="" disabled>Selecciona una opción</option>
          <option value="diario">Diario</option>
          <option value="cada-2-dias">Cada 2 días</option>
          <option value="2-veces-semana">2 veces a la semana</option>
          <option value="1-vez-semana">1 vez a la semana</option>
        </select>

        <label htmlFor="problemasCabello">3. ¿Tienes algún problema específico con tu cabello?</label>
        <textarea
          id="problemasCabello"
          name="problemasCabello"
          rows="3"
          placeholder="Por ejemplo, frizz, caspa, puntas abiertas, caída, etc."
          value={formData.problemasCabello}
          onChange={handleChange}
        ></textarea>

        <label htmlFor="productosUsados">4. ¿Qué productos utilizas habitualmente?</label>
        <textarea
          id="productosUsados"
          name="productosUsados"
          rows="3"
          placeholder="Por ejemplo, champú sin sulfatos, acondicionador hidratante, mascarillas, etc."
          value={formData.productosUsados}
          onChange={handleChange}
        ></textarea>

        <label htmlFor="coloracion">5. ¿Te tiñes o decoloras el cabello con frecuencia?</label>
        <select
          id="coloracion"
          name="coloracion"
          value={formData.coloracion}
          onChange={handleChange}
          required
        >
          <option value="" disabled>Selecciona una opción</option>
          <option value="nunca">Nunca</option>
          <option value="rara-vez">Rara vez</option>
          <option value="regular">Con regularidad</option>
          <option value="muy-frecuente">Muy frecuente</option>
        </select>

        <button type="submit">Enviar cuestionario</button>
      </form>

      <div className="note">
        Al enviar tus datos, te contactaremos por correo electrónico con un diagnóstico personalizado
        y recomendaciones para mejorar la salud de tu cabello.
      </div>
    </div>
  );
}

export default App;
