// components/LanguageButton.js
import React from 'react';
import spanishFlag from '../assets/spanishFlag.png'; // Asegúrate de que esta ruta sea correcta
import englishFlag from '../assets/englishFlag.png'; // Asegúrate de que esta ruta sea correcta

function LanguageButton({ language, toggleLanguage }) {
  return (
    <button className="btn-language" onClick={toggleLanguage}>
      {language === 'es' ? (
        <img src={spanishFlag} alt="Español" className="flag-icon" />
      ) : (
        <img src={englishFlag} alt="English" className="flag-icon" />
      )}
    </button>
  );
}

export default LanguageButton;
