import React from 'react';
import ProductCard from './ProductCard'; // Importamos el nuevo componente

// Array de productos (se mantiene igual que antes)
const productList = [
  {
    id: 1,
    name: "Shampoo Signature",
    description:
      "Un limpiador diario rico, mezclado con ingredientes exclusivos para suavizar, desenredar y proteger el cabello. Prepara tu cabello para el glamour.",
    mainBenefit: "Ligero",
    secondaryBenefits:
      "Limpiador rico y diario, repara y protege el cabello.",
  },
  {
    id: 2,
    name: "Shampoo Equilibrium",
    description:
      "Extiende el tiempo entre lavados. Limpia suavemente con algas marinas verdes y un prebiótico derivado del azúcar para reducir y controlar el exceso de grasa y equilibrar el microbioma del cuero cabelludo.",
    mainBenefit: "Limpieza suave y regulación del exceso de grasa",
    secondaryBenefits:
      "Controla el sebo, prolonga los estilos y equilibra el cuero cabelludo.",
  },
  {
    id: 3,
    name: "Acondicionador Signature",
    description:
      "Hidratante restaurador diario. Polímeros hidratantes que reviven el brillo juvenil, desenredan y facilitan el peinado durante el día.",
    mainBenefit: "Repara y protege",
    secondaryBenefits: "Intenso pero ligero.",
  },
  {
    id: 4,
    name: "Mascarilla Hidratante",
    description:
      "Mascarilla ultra hidratante con aceites de coco y almendras, ideal para cabello rizado, grueso o seco que necesita hidratación intensa y fortaleza.",
    mainBenefit: "Previene la rotura",
    secondaryBenefits:
      "Sella la cutícula, repara daños, restaura elasticidad, suaviza y combate el frizz.",
  },
  {
    id: 5,
    name: "Mascarilla Reconstrucción",
    description:
      "Penetra y reconstruye cada fibra con té blanco, baobab, jazmín y un complejo bio-restaurador. Mejora la elasticidad y fortalece el cabello para brillo y suavidad.",
    mainBenefit: "Nutrición intensa y acondicionamiento",
    secondaryBenefits:
      "Fortalece la cutícula, mejora la elasticidad, restaura el cabello sobreprocesado y lo deja sedoso.",
  },
  {
    id: 6,
    name: "Leave-in Signature",
    description:
      "Bálsamo ligero para definir estilos naturales sin calor. Contiene levadura malt fermentada para un brillo saludable y protección contra daños.",
    mainBenefit: "Define estilos sin calor",
    secondaryBenefits:
      "Deja el cabello suave y pulido, fortalece la fibra capilar, controla el frizz y fortalece el cabello frágil.",
  },
  {
    id: 7,
    name: "Aceite Amplificador",
    description:
      "Aceite ligero de absorción rápida con jazmín, edelweiss, lichi, sándalo, cassis y argán para nutrir y fortalecer el cabello seco, sin brillo y dañado.",
    mainBenefit: "Aceite ligero y lujoso",
    secondaryBenefits:
      "Penetra la fibra capilar, protege contra daños térmicos, reduce el tiempo de secado, aporta brillo y suavidad, evitando apelmazar.",
  },
  {
    id: 8,
    name: "Bálsamo para Rizos",
    description:
      "Gel ligero para formar y dar brillo a los rizos con control hidratante. Contiene extractos brasileños para combatir la humedad y definir sin dejar residuo.",
    mainBenefit: "Fórmula ligera con fijación natural",
    secondaryBenefits:
      "Controla el frizz, hidrata y acondiciona, añade brillo y mantiene la memoria del rizo todo el día.",
  },
];

/**
 * Función auxiliar para buscar un producto por su "name" dentro de la lista.
 */
function getProductByName(name) {
  return productList.find((p) => p.name === name);
}

function FinalScreen({ formData, language }) {

  const getRecommendations = (data) => {
    let recs = [];

    // Cabello seco o quebradizo
    if (data.condicionGeneral === 'seco-quebradizo') {
      recs.push(getProductByName('Mascarilla Hidratante'));
      recs.push(getProductByName('Aceite Amplificador'));
    }

    // Cabello graso
    if (data.condicionGeneral === 'graso') {
      recs.push(getProductByName('Shampoo Equilibrium'));
    }

    // Falta de volumen
    if ((data.preocupacionesCapilares || []).includes('falta-volumen')) {
      recs.push(getProductByName('Leave-in Signature'));
    }

    // Frizz y falta de control
    if ((data.preocupacionesCapilares || []).includes('frizz')) {
      recs.push(getProductByName('Bálsamo para Rizos'));
      recs.push(getProductByName('Aceite Amplificador'));
    }

    // Cabello teñido o dañado
    if (
      data.tratamientosQuimicos === 'tinte-decoloracion' ||
      data.tratamientosQuimicos === 'alisado-permanente'
    ) {
      recs.push(getProductByName('Mascarilla Reconstrucción'));
      recs.push(getProductByName('Acondicionador Signature'));
    }

    // Lavado frecuente
    if (
      data.frecuenciaLavado === 'diario' ||
      data.frecuenciaLavado === 'cada-dos-dias'
    ) {
      recs.push(getProductByName('Shampoo Signature'));
      recs.push(getProductByName('Shampoo Equilibrium'));
    }

    // Cabello expuesto a calor
    if (
      data.usoCalor === 'ocasional' ||
      data.usoCalor === 'frecuente'
    ) {
      recs.push(getProductByName('Aceite Amplificador'));
      recs.push(getProductByName('Leave-in Signature'));
    }

    // Elimina duplicados y falsos
    const uniqueRecs = recs.filter(Boolean);
    return [...new Set(uniqueRecs)];
  };

  const recommendations = getRecommendations(formData);

  return (
    <div className="final-screen">
      <h1>
        {language === 'es'
          ? `¡Gracias, ${formData.nombre || 'Usuario'}!`
          : `Thank you, ${formData.nombre || 'User'}!`}
      </h1>
      <p>
        {language === 'es'
          ? 'Basado en tus respuestas, te recomendamos:'
          : 'Based on your answers, we recommend:'}
      </p>

      {/* En vez de <ul>, renderizamos un componente por cada producto */}
      {recommendations.map((product) => (
        <ProductCard
          key={product.id}
          product={product}
          language={language}
        />
      ))}
    </div>
  );
}

export default FinalScreen;
