// src/App.js
import React, { useState } from 'react';
import './App.css';

// No import de imágenes, solo fondo negro
import Questionnaire from './components/Questionnaire';

function App() {
  const [showQuestionnaire, setShowQuestionnaire] = useState(false);

  const handleStart = () => {
    setShowQuestionnaire(true);
  };

  // Usamos backgroundColor en lugar de backgroundImage
  const backgroundStyle = {
    backgroundColor: '#000',  // fondo negro
    minHeight: '100vh',
  };

  return (
    <div style={backgroundStyle}>
      <div className="app-container">
        {showQuestionnaire ? (
          <Questionnaire />
        ) : (
          <SplashScreen onStart={handleStart} />
        )}
      </div>
    </div>
  );
}

function SplashScreen({ onStart }) {
  return (
    <div className="splash-screen">
      {/* Logo opcional: se asume fi-logo.png en /public */}
      <img 
        src="/fi-logo.png" 
        alt="Logo Francisco Iglesias" 
        className="logo-animado" 
      />
      <button className="btn-empezar" onClick={onStart}>
        Empezar Cuestionario →
      </button>
    </div>
  );
}

export default App;
