// Questionnaire.jsx
import React, { useState } from 'react';
import { questions } from './questionsData';
import ProgressBar from './ProgressBar';
import FinalScreen from './FinalScreen';

function Questionnaire({ language }) {
  const [currentStep, setCurrentStep] = useState(0);
  const [submitted, setSubmitted] = useState(false);

  // Inicializamos formData con la estructura según las preguntas
  const [formData, setFormData] = useState(() => {
    const initial = {};
    questions.forEach((q) => {
      // Para preguntas tipo checkboxGroup, usamos un array vacío
      if (q.type === 'checkboxGroup') {
        initial[q.name] = [];
      } else {
        initial[q.name] = '';
      }
    });
    return initial;
  });

  // Maneja el cambio en inputs
  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;

    if (type === 'checkbox') {
      // Grupo de checkboxes
      setFormData((prev) => {
        const currentArr = prev[name] || [];
        if (checked) {
          // Agregamos el valor
          return { ...prev, [name]: [...currentArr, value] };
        } else {
          // Quitamos el valor
          return { ...prev, [name]: currentArr.filter((v) => v !== value) };
        }
      });
    } else {
      // Input normal (text, select, etc.)
      setFormData((prev) => ({ ...prev, [name]: value }));
    }
  };

  // Maneja el botón "Siguiente"
  const handleNext = () => {
    // Obtenemos la pregunta actual
    const question = questions[currentStep];

    // Verificamos si la pregunta actual define un minSelections
    if (
      question.type === 'checkboxGroup' &&
      question.minSelections &&
      formData[question.name].length < question.minSelections
    ) {
      alert(
        language === 'es'
          ? `Debes seleccionar al menos ${question.minSelections} opciones.`
          : `You must select at least ${question.minSelections} options.`
      );
      return;
    }

    // Si pasa la validación, avanzamos
    if (currentStep < questions.length - 1) {
      setCurrentStep((prev) => prev + 1);
    }
  };

  // Maneja el botón "Atrás"
  const handlePrev = () => {
    if (currentStep > 0) {
      setCurrentStep((prev) => prev - 1);
    }
  };

  // Maneja el submit final
  const handleSubmit = (e) => {
    e.preventDefault();
    // Aquí podrías poner más validaciones si gustas
    setSubmitted(true);
  };

  // Si el cuestionario ya fue enviado, mostramos la pantalla final
  if (submitted) {
    return <FinalScreen formData={formData} language={language} />;
  }

  // Obtenemos la pregunta actual
  const question = questions[currentStep];

  // Renderiza el input según el tipo de la pregunta
  const renderInput = (q) => {
    switch (q.type) {
      case 'text':
      case 'email':
      case 'number':
        return (
          <input
            type={q.type}
            name={q.name}
            placeholder={q.placeholder || ''}
            required={q.required}
            value={formData[q.name]}
            onChange={handleChange}
          />
        );
      case 'textarea':
        return (
          <textarea
            name={q.name}
            placeholder={q.placeholder || ''}
            rows="4"
            required={q.required}
            value={formData[q.name]}
            onChange={handleChange}
          />
        );
      case 'select':
        return (
          <select
            name={q.name}
            required={q.required}
            value={formData[q.name]}
            onChange={handleChange}
          >
            {q.options.map((opt) => (
              <option
                key={opt.value}
                value={opt.value}
                disabled={opt.disabled || false}
              >
                {opt.label}
              </option>
            ))}
          </select>
        );
      case 'checkboxGroup':
        return (
          <div className="checkbox-group">
            {q.options.map((opt) => (
              <label key={opt.value} style={{ display: 'block' }}>
                <input
                  type="checkbox"
                  name={q.name}
                  value={opt.value}
                  checked={formData[q.name].includes(opt.value)}
                  onChange={handleChange}
                />
                {opt.label}
              </label>
            ))}
          </div>
        );
      default:
        return null;
    }
  };

  return (
    <div className="container">
      <ProgressBar currentStep={currentStep} totalSteps={questions.length} />
      <h1>
        {language === 'es'
          ? 'Cuestionario de Diagnóstico Personalizado para Haircare'
          : 'Personalized Haircare Diagnosis Questionnaire'}
      </h1>
      <h2>
        {language === 'es'
          ? `Pregunta ${currentStep + 1} de ${questions.length}`
          : `Question ${currentStep + 1} of ${questions.length}`}
      </h2>

      <form onSubmit={handleSubmit}>
        <label htmlFor={question.name}>{question.question}</label>
        {renderInput(question)}

        <div className="buttons" style={{ marginTop: '1rem' }}>
          {currentStep > 0 && (
            <button type="button" onClick={handlePrev}>
              {language === 'es' ? 'Atrás' : 'Back'}
            </button>
          )}

          {currentStep < questions.length - 1 ? (
            <button type="button" onClick={handleNext}>
              {language === 'es' ? 'Siguiente' : 'Next'}
            </button>
          ) : (
            <button type="submit">
              {language === 'es' ? 'Finalizar' : 'Finish'}
            </button>
          )}
        </div>
      </form>
    </div>
  );
}

export default Questionnaire;
