import React from 'react';

// Lista de productos (versión simple en español). 
// Ajusta según tu estructura real.
const productList = [
  {
    id: 1,
    name: "Mascarilla Hidratante",
    description: "Repara y humecta el cabello seco o rizado de forma intensa.",
    mainBenefit: "Previene la rotura",
    secondaryBenefits: "Sella la cutícula, repara daños, restaura elasticidad, combate el frizz.",
  },
  {
    id: 2,
    name: "Aceite Amplificador",
    description: "Aceite ligero que nutre, protege del calor y aporta brillo.",
    mainBenefit: "Aceite ligero y lujoso",
    secondaryBenefits: "Penetra la fibra, reduce tiempo de secado, evita apelmazar.",
  },
  {
    id: 3,
    name: "Shampoo Equilibrium",
    description: "Limpia suavemente, regula exceso de grasa y prolonga el tiempo entre lavados.",
    mainBenefit: "Control de sebo",
    secondaryBenefits: "Equilibra el microbioma del cuero cabelludo, reduce grasa.",
  },
  // ... Resto de tus productos
];

/**
 * Función auxiliar para buscar un producto por su "name"
 */
function getProductByName(name) {
  return productList.find((p) => p.name === name);
}

function FinalScreen({ formData, language }) {
  const getRecommendations = (data) => {
    const recs = [];

    // 1) Cabello seco/quebradizo
    if (data.condicionGeneral === 'seco-quebradizo') {
      recs.push({
        product: getProductByName('Mascarilla Hidratante'),
        reason:
          language === 'es'
            ? "Porque tu cabello es seco/quebradizo y necesita hidratación intensa."
            : "Because your hair is dry/brittle and needs intense hydration.",
      });
      recs.push({
        product: getProductByName('Aceite Amplificador'),
        reason:
          language === 'es'
            ? "Ayuda a sellar la humedad y proteger el cabello frágil."
            : "Helps seal in moisture and protect fragile hair.",
      });
    }

    // 2) Cabello graso
    if (data.condicionGeneral === 'graso') {
      recs.push({
        product: getProductByName('Shampoo Equilibrium'),
        reason:
          language === 'es'
            ? "Controla el exceso de grasa y equilibra el cuero cabelludo."
            : "Controls excess oil and balances the scalp.",
      });
    }

    // ... Resto de reglas y razones para cada caso
    // Ejemplo: Falta de volumen, Frizz, Cabello teñido, etc.

    // Eliminamos duplicados (product) si aparecen repetidos
    const uniqueSet = new Map();
    for (const item of recs) {
      // key = product.name
      if (!uniqueSet.has(item.product?.name)) {
        uniqueSet.set(item.product?.name, item);
      } else {
        // Si ya existe, podríamos concatenar razones (opcional)
        // uniqueSet.get(item.product.name).reason += " + " + item.reason;
      }
    }
    return Array.from(uniqueSet.values());
  };

  const recommendations = getRecommendations(formData);

  return (
    <div className="final-screen">
      <h1>
        {language === 'es'
          ? `¡Gracias, ${formData.nombre || 'Usuario'}!`
          : `Thank you, ${formData.nombre || 'User'}!`}
      </h1>
      <p>
        {language === 'es'
          ? 'Basado en tus respuestas, te recomendamos:'
          : 'Based on your answers, we recommend:'}
      </p>

      {/* Lista de productos con su justificación */}
      {recommendations.map(({ product, reason }, idx) => {
        if (!product) return null;
        return (
          <div key={idx} style={{ marginBottom: '1rem' }}>
            <strong>{product.name}</strong>
            <br />
            <em>{product.description}</em>
            <br />
            <strong>
              {language === 'es' ? 'Razón:' : 'Reason:'}
            </strong>{' '}
            {reason}
            <br />
            <strong>
              {language === 'es'
                ? 'Beneficio Principal:'
                : 'Main Benefit:'}
            </strong>{' '}
            {product.mainBenefit}
            <br />
            <strong>
              {language === 'es'
                ? 'Beneficios Secundarios:'
                : 'Secondary Benefits:'}
            </strong>{' '}
            {product.secondaryBenefits}
          </div>
        );
      })}
    </div>
  );
}

export default FinalScreen;
