import React, { useState } from 'react';
import './App.css';

// IMPORTAMOS la textura de cuero gris oscuro
import darkLeather from './assets/blueLeather.jpg';

import Questionnaire from './components/Questionnaire';

function App() {
  const [showQuestionnaire, setShowQuestionnaire] = useState(false);

  const handleStart = () => {
    setShowQuestionnaire(true);
  };

  // Estilos de fondo con la textura gris oscuro y un overlay gris transparente
  const backgroundStyle = {
    backgroundImage: `linear-gradient(rgba(50, 50, 50, 0.5), rgba(50, 50, 50, 0.5)), url(${darkLeather})`,
    backgroundRepeat: 'no-repeat',
    backgroundSize: 'cover',
    backgroundPosition: 'center center',
    backgroundAttachment: 'fixed',
    minHeight: '100vh',
  };

  return (
    <div style={backgroundStyle}>
      <div className="app-container">
        {showQuestionnaire ? (
          <Questionnaire />
        ) : (
          <SplashScreen onStart={handleStart} />
        )}
      </div>
    </div>
  );
}

function SplashScreen({ onStart }) {
  return (
    <div className="splash-screen">
      <img 
        src="/fi-logo.png" 
        alt="Logo Francisco Iglesias" 
        className="logo-animado" 
      />

      <button className="btn-empezar" onClick={onStart}>
        Empezar Cuestionario
      </button>
    </div>
  );
}

export default App;
