import leatherBG from './assets/leather.jpg';

function App() {
  // 2) Creamos un objeto de estilo con el background
  const backgroundStyle = {
    backgroundImage: `url(${leatherBG})`,
    backgroundRepeat: 'no-repeat',
    backgroundSize: 'cover',
    backgroundPosition: 'center center',
    backgroundAttachment: 'fixed', // opcional, para fondo fijo al hacer scroll
    minHeight: '100vh',            // para que ocupe pantalla completa
  };

  return (
    <div style={backgroundStyle}>
      <h1 style={{ color: 'white', textAlign: 'center', paddingTop: '50px' }}>
