// src/components/questionsData.js
export const questions = [
    {
      id: 1,
      type: "text",
      name: "nombre",
      question: "¿Cuál es tu nombre completo?",
      placeholder: "Ingresa tu nombre",
      required: true
    },
    {
      id: 2,
      type: "email",
      name: "email",
      question: "¿Cuál es tu correo electrónico?",
      placeholder: "tucorreo@ejemplo.com",
      required: true
    },
    {
      id: 3,
      type: "select",
      name: "tipoCabello",
      question: "¿Cómo describirías tu tipo de cabello?",
      required: true,
      options: [
        { value: "", label: "Selecciona una opción", disabled: true },
        { value: "seco", label: "Seco" },
        { value: "graso", label: "Graso" },
        { value: "normal", label: "Normal" },
        { value: "mixto", label: "Mixto" }
      ]
    },
    {
      id: 4,
      type: "select",
      name: "frecuenciaLavado",
      question: "¿Con qué frecuencia lavas tu cabello?",
      required: true,
      options: [
        { value: "", label: "Selecciona una opción", disabled: true },
        { value: "diario", label: "Diario" },
        { value: "cada-2-dias", label: "Cada 2 días" },
        { value: "2-veces-semana", label: "2 veces a la semana" },
        { value: "1-vez-semana", label: "1 vez a la semana" }
      ]
    },
    {
      id: 5,
      type: "textarea",
      name: "problemasCabello",
      question: "¿Tienes algún problema específico con tu cabello?",
      placeholder: "Por ejemplo, frizz, caspa, puntas abiertas, caída, etc."
    },
    {
      id: 6,
      type: "textarea",
      name: "productosUsados",
      question: "¿Qué productos utilizas habitualmente?",
      placeholder: "Por ejemplo, champú sin sulfatos, acondicionador hidratante, mascarillas, etc."
    },
    {
      id: 7,
      type: "select",
      name: "coloracion",
      question: "¿Te tiñes o decoloras el cabello con frecuencia?",
      required: true,
      options: [
        { value: "", label: "Selecciona una opción", disabled: true },
        { value: "nunca", label: "Nunca" },
        { value: "rara-vez", label: "Rara vez" },
        { value: "regular", label: "Con regularidad" },
        { value: "muy-frecuente", label: "Muy frecuente" }
      ]
    }
  ];
