// ProductCard.jsx
import React from 'react';
import './ProductCard.css'; // Hoja de estilos para la tarjeta (opcional)

function ProductCard({ product, language }) {
  return (
    <div className="product-card">
      <h3 className="product-card__title">{product.name}</h3>
      <p className="product-card__description">
        <em>{product.description}</em>
      </p>
      <p>
        <strong>
          {language === 'es' ? 'Beneficio Principal:' : 'Main Benefit:'}
        </strong>{' '}
        {product.mainBenefit}
      </p>
      <p>
        <strong>
          {language === 'es' ? 'Beneficios Secundarios:' : 'Secondary Benefits:'}
        </strong>{' '}
        {product.secondaryBenefits}
      </p>
    </div>
  );
}

export default ProductCard;
