// FinalScreen.jsx
import React from 'react';
import ProductCard from './ProductCard'; // Horizontal card component

/**
 * Bilingual product list.
 * Each product has an "id", plus fields in English ("en") and Spanish ("es").
 */
const productList = [
  {
    internalId: "Hydrating Mask",
    es: {
      name: "Mascarilla Hidratante",
      description: "Repara y humecta el cabello seco o quebradizo de forma intensa.",
      mainBenefit: "Previene la rotura",
      secondaryBenefits: "Sella la cutícula, repara daños, restaura elasticidad, combate el frizz.",
    },
    en: {
      name: "Hydrating Mask",
      description: "Deeply moisturizes and revives dry or brittle hair.",
      mainBenefit: "Prevents breakage",
      secondaryBenefits: "Seals the cuticle, repairs damage, restores elasticity, fights frizz.",
    },
  },
  {
    internalId: "Amplifying Oil",
    es: {
      name: "Aceite Amplificador",
      description: "Aceite ligero que nutre, protege del calor y aporta brillo.",
      mainBenefit: "Aceite ligero y lujoso",
      secondaryBenefits: "Penetra la fibra, reduce tiempo de secado, evita apelmazar.",
    },
    en: {
      name: "Amplifying Oil",
      description: "Lightweight oil that nourishes, protects from heat, and adds shine.",
      mainBenefit: "Luxurious lightweight oil",
      secondaryBenefits: "Penetrates hair shaft, reduces drying time, and avoids weigh-down.",
    },
  },
  {
    internalId: "Equilibrium Shampoo",
    es: {
      name: "Shampoo Equilibrium",
      description: "Limpia suavemente, regula exceso de grasa y extiende el tiempo entre lavados.",
      mainBenefit: "Regula el exceso de grasa",
      secondaryBenefits: "Controla sebo, equilibra el cuero cabelludo, prolonga estilos.",
    },
    en: {
      name: "Equilibrium Shampoo",
      description: "Gently cleanses, regulates excess oil, and extends time between washes.",
      mainBenefit: "Regulates excess oil",
      secondaryBenefits: "Controls sebum, balances scalp, prolongs styles.",
    },
  },
  // ...Add more products with both "es" and "en" versions
];

/**
 * Helper function to find a product by 'internalId'.
 * We then pick either the "es" or "en" object based on language.
 */
function getProduct(internalId, language) {
  const baseProduct = productList.find((p) => p.internalId === internalId);
  if (!baseProduct) return null;
  return baseProduct[language] || null;
}

function FinalScreen({ formData, language }) {
  /**
   * getRecommendations:
   * Returns an array of { product, reason } objects.
   * The 'product' is language-specific.
   */
  const getRecommendations = (data) => {
    const recs = [];

    // Example rule: If hair is dry/brittle -> recommend mask + oil
    if (data.condicionGeneral === 'seco-quebradizo') {
      recs.push({
        product: getProduct('Hydrating Mask', language),
        reason:
          language === 'es'
            ? "Porque tu cabello es seco/quebradizo y necesita hidratación intensa."
            : "Because your hair is dry/brittle and needs intense hydration.",
      });
      recs.push({
        product: getProduct('Amplifying Oil', language),
        reason:
          language === 'es'
            ? "Ayuda a sellar la humedad y proteger el cabello frágil."
            : "Helps lock in moisture and protect fragile hair.",
      });
    }

    // Another rule: If hair is oily -> Equilibrium shampoo
    if (data.condicionGeneral === 'graso') {
      recs.push({
        product: getProduct('Equilibrium Shampoo', language),
        reason:
          language === 'es'
            ? "Controla el exceso de grasa y equilibra tu cuero cabelludo."
            : "Controls excess oil and balances your scalp.",
      });
    }

    // ...Add more logic for other hair conditions

    // Remove duplicates based on product.name (just in case)
    const unique = new Map();
    for (const item of recs) {
      if (!item.product) continue;
      if (!unique.has(item.product.name)) {
        unique.set(item.product.name, item);
      }
    }
    return Array.from(unique.values());
  };

  const recommendations = getRecommendations(formData);

  return (
    <div className="final-screen">
      <h1>
        {language === 'es'
          ? `¡Gracias, ${formData.nombre || 'Usuario'}!`
          : `Thank you, ${formData.nombre || 'User'}!`}
      </h1>

      <p>
        {language === 'es'
          ? 'Basado en tus respuestas, te recomendamos:'
          : 'Based on your answers, we recommend:'}
      </p>

      {/* Container for horizontal cards */}
      <div className="final-screen__cards-container">
        {recommendations.map(({ product, reason }, index) => {
          if (!product) return null;
          return (
            <ProductCard
              key={index}
              product={product}
              reason={reason}
              language={language}
            />
          );
        })}
      </div>
    </div>
  );
}

export default FinalScreen;
