import React from 'react';
import productImg from '../assets/h (1).png';

function FinalScreen({ formData, language }) {

  // Simple function to pick recommended products
  const getRecommendations = (data) => {
    let recs = [];

    // 1) Dry or brittle
    if (data.condicionGeneral === 'seco-quebradizo') {
      recs.push('Mascarilla Hidratante', 'Aceite Amplificador');
    }

    // 2) Oily
    if (data.condicionGeneral === 'graso') {
      recs.push('Shampoo Equilibrium');
    }

    // 3) Lack of volume
    if ((data.preocupacionesCapilares || []).includes('falta-volumen')) {
      recs.push('Leave-in Signature');
    }

    // 4) Frizz
    if ((data.preocupacionesCapilares || []).includes('frizz')) {
      recs.push('Bálsamo para Rizos', 'Aceite Amplificador');
    }

    // 5) Dyed or chemically treated
    if (
      data.tratamientosQuimicos === 'tinte-decoloracion' ||
      data.tratamientosQuimicos === 'alisado-permanente'
    ) {
      recs.push('Mascarilla Reconstrucción', 'Acondicionador Signature');
    }

    // 6) Frequent washing
    if (
      data.frecuenciaLavado === 'diario' ||
      data.frecuenciaLavado === 'cada-dos-dias'
    ) {
      recs.push('Shampoo Signature', 'Shampoo Equilibrium');
    }

    // 7) Uses heat
    if (
      data.usoCalor === 'ocasional' ||
      data.usoCalor === 'frecuente'
    ) {
      recs.push('Aceite Amplificador', 'Leave-in Signature');
    }

    // remove duplicates
    const uniqueRecs = [...new Set(recs)];
    return uniqueRecs;
  };

  const recommendations = getRecommendations(formData);

  return (
    <div className="final-screen">
      <h1>
        {language === 'es'
          ? `¡Gracias, ${formData.nombre || 'Usuario'}!`
          : `Thank you, ${formData.nombre || 'User'}!`}
      </h1>

      <p>
        {language === 'es'
          ? 'Basado en tus respuestas, te recomendamos:'
          : 'Based on your answers, we recommend:'}
      </p>

      {/* Display recommended products */}
      <ul style={{ textAlign: 'left' }}>
        {recommendations.map((rec, idx) => (
          <li key={idx}>{rec}</li>
        ))}
      </ul>

      <div className="product-image-container">
        <img
          src={productImg}
          alt="Producto Recomendado"
          className="product-image"
        />
      </div>

      <hr style={{ margin: '2rem 0' }} />

      {/* Example references */}
      <p><strong>Sección 5: Recomendaciones Personalizadas</strong></p>
      <p>
        <strong>Cabello seco o quebradizo:</strong> Mascarilla Hidratante o Aceite Amplificador <br/>
        <strong>Cabello graso:</strong> Shampoo Equilibrium<br/>
        <strong>Falta de volumen:</strong> Leave-in Signature<br/>
        <strong>Frizz:</strong> Bálsamo para Rizos o Aceite Amplificador<br/>
        <strong>Cabello teñido o dañado:</strong> Mascarilla Reconstrucción o Acondicionador Signature<br/>
        <strong>Lavado frecuente:</strong> Shampoo Signature o Equilibrium<br/>
        <strong>Cabello expuesto a calor:</strong> Aceite Amplificador o Leave-in Signature
      </p>
    </div>
  );
}

export default FinalScreen;
