// src/App.js
import React, { useState } from 'react';
import Questionnaire from './components/Questionnaire';

function App() {
  const [showQuestionnaire, setShowQuestionnaire] = useState(false);

  const handleStart = () => {
    setShowQuestionnaire(true);
  };

  return (
    <div className="app-container">
      {showQuestionnaire ? (
        <Questionnaire />
      ) : (
        <SplashScreen onStart={handleStart} />
      )}
    </div>
  );
}

/** SplashScreen: Muestra el logo, el título, y un botón para empezar el cuestionario. */
function SplashScreen({ onStart }) {
  return (
    <div className="splash-screen">
      {/* Ajusta la ruta de tu imagen si cambias el nombre o ubicación */}
      <img src="/fi-logo.png" alt="Logo Francisco Iglesias" className="logo-animado" />
      <h1 className="titulo-splash">franciscoiglesias.cuestionario</h1>
      <button className="btn-empezar" onClick={onStart}>
        Empezar Cuestionario
      </button>
    </div>
  );
}

export default App;
