// src/components/FinalScreen.js
import React, { useState, useEffect } from 'react';
import productImg from '../assets/h (1).png';
import Confetti from 'react-confetti';

function FinalScreen({ formData }) {
  const [dimensions, setDimensions] = useState({
    width: window.innerWidth,
    height: window.innerHeight
  });

  useEffect(() => {
    const handleResize = () => {
      setDimensions({ width: window.innerWidth, height: window.innerHeight });
    };
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  return (
    <div className="final-screen">
      <Confetti
        width={dimensions.width}
        height={dimensions.height}
        colors={['#ffffff']}       // Confetti en color blanco
        numberOfPieces={20}         // Solo 20 piezas para que sean pocas
        recycle={false}             // No se recicla, se muestra una única vez
      />
      <h1>¡Gracias, {formData.nombre || 'Usuario'}!</h1>
      <p>Basándonos en tus respuestas, te recomendamos el siguiente producto:</p>
      <div className="product-image-container">
        <img 
          src={productImg} 
          alt="Producto Recomendado" 
          className="product-image" 
        />
      </div>
      <div className="summary">
        <h2>Resumen de tus respuestas:</h2>
        <ul>
          <li><strong>Tipo de Cabello:</strong> {formData.tipoCabello}</li>
          <li><strong>Frecuencia de Lavado:</strong> {formData.frecuenciaLavado}</li>
          {formData.problemasCabello && <li><strong>Problemas:</strong> {formData.problemasCabello}</li>}
          {formData.productosUsados && <li><strong>Productos Usados:</strong> {formData.productosUsados}</li>}
          <li><strong>Coloración:</strong> {formData.coloracion}</li>
        </ul>
      </div>
    </div>
  );
}

export default FinalScreen;
